DSD Markup

&lt;!-- &lt;kr-layout&gt; DSD -&gt;
&lt;!-- This file is auto-generated --&gt;
&lt;template shadowrootmode="open"&gt;
      &lt;!-- version: 0.0.2 --&gt;
      &lt;style&gt;
        *,
    *:after,
    *:before {
      box-sizing: border-box;
    }

    :host(:dir(rtl)) *,
    :host(:dir(rtl)) *:after,
    :host(:dir(rtl)) *:before {
      direction: rtl;
    }

    :host(:dir(ltr)) *,
    :host(:dir(ltr)) *:after,
    :host(:dir(ltr)) *:before {
      direction: ltr;
    }

    .visually-hidden:not(:focus-within) {
      position: absolute;
      overflow: hidden;
      clip: rect(0 0 0 0);
      clip-path: inset(50%);
      width: 1px;
      height: 1px;
      white-space: nowrap;
    }

    [hidden] {
      display: none !important;
    }

    :host {
      display: block;
      box-sizing: border-box;
      min-height: var(--height);
      --height: 100vh;
      --height: 100dvh;

      --menu-width: auto;
      --main-width: 1fr;
      --aside-width: auto;

      /** This is a best guess. We'll attempt to calculate this with a resize observer. **/
      --header-height: 68.33px;
    }

    :host([variant="documentation"]) {
      --menu-width: 250px;
      --main-width: 105ch;
      --aside-width: auto;
    }

    :host([variant="documentation"])::part(body) {
      justify-content: center;
    }

    *,
    *:after,
    *:before {
      box-sizing: border-box;
    }

    :host::part(base) {
      display: grid;
      /** Header, Main, Footer **/
      grid-template-rows: minmax(0, auto) minmax(0, 1fr) minmax(0, auto);
      min-height: var(--height);
    }

    :host::part(header) {
      max-width: 100%;
      position: sticky;
      background: white;
      z-index: 1;
    }

    :is(.header, .aside, .menu, .footer) ::slotted(*) {
      height: 100%;
    }

    :is(.aside, .menu) ::slotted(*) {
      min-width: 100%;
      width: 100%;
      max-width: 100%;
    }

    :host::part(header) {
      top: 0px;
    }

    :host::part(body) {
      display: grid;
      /** Menu, Main, Aside **/
      grid-template-columns: minmax(0, var(--menu-width)) minmax(
          0,
          var(--main-width)
        ) minmax(0, var(--aside-width));
      grid-template-rows: minmax(0, 1fr);
    }

    :host::part(aside),
    :host::part(menu) {
      display: grid;
      grid-template-columns: minmax(0, 1fr);
      max-height: calc(var(--height) - var(--header-height));
      overflow: auto;
      position: sticky;
      top: var(--header-height);
      overscroll-behavior: contain;
    }

    :host([disable-sticky~="header"]) {
      --header-height: 0px;
    }

    :host([disable-sticky~="header"])::part(header) {
      position: static;
    }

    :host([disable-sticky~="aside"])::part(aside),
    :host([disable-sticky~="menu"])::part(menu) {
      position: static;
    }

    :host::part(main) {
      position: relative;
      display: grid;
      grid-template-columns: minmax(0, 1fr);
      /* main-header, main, main-footer */
      grid-template-rows: minmax(0, auto) minmax(0, 1fr) minmax(0, auto);
    }

    :host::part(footer) {
    }

    .skip-links {
      position: absolute;
      top: 0;
      left: 0;
      height: calc(var(--header-height, 48px) - 2px);
      width: 100vw;
      z-index: 4;
      background-color: inherit;
      display: grid;
      grid-template-columns: minmax(0, 1fr);
      place-items: center;
      text-align: center;
    }
      &lt;/style&gt;

      &lt;!-- renderHTML() --&gt;
      &lt;div class="visually-hidden skip-links" part="skip-links"&gt;
    &lt;slot name="skip-links"&gt; &lt;/slot&gt;
  &lt;/div&gt;

  &lt;div class="base" part="base"&gt;
    &lt;div class="header" part="header"&gt;
      &lt;slot name="header"&gt;&lt;/slot&gt;
    &lt;/div&gt;

    &lt;div class="body" part="body"&gt;
      &lt;div class="menu" part="menu"&gt;
        &lt;slot name="menu"&gt;&lt;/slot&gt;
      &lt;/div&gt;

      &lt;div class="main" part="main"&gt;
        &lt;div class="main-header" part="main-header"&gt;
          &lt;slot name="main-header"&gt;&lt;/slot&gt;
        &lt;/div&gt;

        &lt;div class="main-content" part="main-content"&gt;&lt;slot&gt;&lt;/slot&gt;&lt;/div&gt;

        &lt;div class="main-footer" part="main-footer"&gt;
          &lt;slot name="main-footer"&gt;&lt;/slot&gt;
        &lt;/div&gt;
      &lt;/div&gt;

      &lt;div class="aside" part="aside"&gt;
        &lt;slot name="aside"&gt;&lt;/slot&gt;
      &lt;/div&gt;
    &lt;/div&gt;

    &lt;div class="footer" part="footer"&gt;
      &lt;slot name="footer"&gt;&lt;/slot&gt;
    &lt;/div&gt;
  &lt;/div&gt;

  &lt;div part="dialog" class="dialog"&gt;
    &lt;slot name="dialog"&gt;&lt;/slot&gt;
  &lt;/div&gt;
    &lt;/template&gt;

NPM Installation

npm install kr-layout


NPM Usage

// entrypoint.js
import "kr-layout";


CDN usage

&lt;script type="module"&gt;
  import "https://cdn.jsdelivr.net/npm/kr-layout";

  // Loading a version range. This will load the latest &gt;= 1.0.0 and &lt; 2.0.0 of kr-layout.
  import "https://cdn.jsdelivr.net/npm/kr-layout@^1.0.0";

  // Loading a specific version. This will only load v1.0.0
  import "https://cdn.jsdelivr.net/npm/kr-layout@1.0.0";
&lt;/script&gt;


